import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setColorScheme } from '../colorSchemeSlice';

const SettingsPage = () => {
  const dispatch = useDispatch();
  const colorScheme = useSelector((state) => state.colorScheme);
  const {
    backgroundColor,
    primaryColor,
    secondaryColor,
    fontSize,
    fontColor,
  } = colorScheme;


  const handleSchemeChange = (scheme) => {
    switch (scheme) {
      case 'scheme1':
        dispatch(
          setColorScheme({
            backgroundColor: '#a2f5a2',
            primaryColor: '#007bff',
            secondaryColor: '#ff08b5',
            fontSize: '16px',
            fontColor: '#333',
          })
        );
        break;
      case 'scheme2':
        dispatch(
          setColorScheme({
            backgroundColor: '#743bfa',
            primaryColor: '#4caf50',
            secondaryColor: '#d45003',
            fontSize: '18px',
            fontColor: '#222',
          })
        );
        break;
      case 'scheme3':
        dispatch(
          setColorScheme({
            backgroundColor: '#249beb',
            primaryColor: '#9c27b0',
            secondaryColor: '#2196f3',
            fontSize: '14px',
            fontColor: '#555',
          })
        );
        break;
      case 'scheme4':
        dispatch(
          setColorScheme({
            backgroundColor: '#f334da',
            primaryColor: '#ff5722',
            secondaryColor: '#bebe00',
            fontSize: '20px',
            fontColor: '#111',
          })
        );
        break;
      case 'scheme5':
        dispatch(
          setColorScheme({
            backgroundColor: '#c5f124',
            primaryColor: '#e91e63',
            secondaryColor: '#00bcd4',
            fontSize: '22px',
            fontColor: '#444',
          })
        );
        break;
      default:
        dispatch(
          setColorScheme({
            backgroundColor: '',
            primaryColor: '',
            secondaryColor: '',
            fontSize: '',
            fontColor: '',
          })
        );
    }

  };

  return (
    <div style={{
      backgroundColor: backgroundColor,
      color: fontColor,
    }}>
      <header className="header2" div style={{
      backgroundColor: s}}>Settings</header>
      <div className="content">
        <div className="color-scheme-buttons">
          <button
            className={`color-scheme-btn1 ${
              colorScheme.backgroundColor === '#a2f5a2' ? 'active' : ''
            }`}
            style={{ backgroundColor: '#a2f5a2' }}
            onClick={() => handleSchemeChange('scheme1')}
          />
          <button
            className={`color-scheme-btn2 ${
              colorScheme.backgroundColor === '#743bfa' ? 'active' : ''
            }`}
            style={{ backgroundColor: '#743bfa' }}
            onClick={() => handleSchemeChange('scheme2')}
          />
          <button
            className={`color-scheme-btn3 ${
              colorScheme.backgroundColor === '#249beb' ? 'active' : ''
            }`}
            style={{ backgroundColor: '#249beb' }}
            onClick={() => handleSchemeChange('scheme3')}
          />
          <button
            className={`color-scheme-btn4 ${
              colorScheme.backgroundColor === '#f334da' ? 'active' : ''
            }`}
            style={{ backgroundColor: '#f334da' }}
            onClick={() => handleSchemeChange('scheme4')}
          />
          <button
            className={`color-scheme-btn5 ${
              colorScheme.backgroundColor === '#c5f124' ? 'active' : ''
            }`}
            style={{ backgroundColor: '#c5f124' }}
            onClick={() => handleSchemeChange('scheme5')}
          />
        </div>
        <div className='bott'>
        <label>
          Background Color:
          <input
            type="color"
            value={colorScheme.backgroundColor}
            onChange={(e) =>
              dispatch(
                setColorScheme({
                  ...colorScheme,
                  backgroundColor: e.target.value,
                })
              )
            }
          />
        </label>
        <label>
          Primary Color:
          <input
            type="color"
            value={colorScheme.primaryColor}
            onChange={(e) =>
              dispatch(
                setColorScheme({
                  ...colorScheme,
                  primaryColor: e.target.value,
                })
              )
            }
          />
        </label>
        <label>
          Secondary Color:
          <input
            type="color"
            value={colorScheme.secondaryColor}
            onChange={(e) =>
              dispatch(
                setColorScheme({
                  ...colorScheme,
                  secondaryColor: e.target.value,
                })
              )
            }
          />
        </label>
        <label>
          Font Size:
          <input
            type="number"
            min="12"
            max="24"
            value={colorScheme.fontSize}
            onChange={(e) =>
              dispatch(
                setColorScheme({
                  ...colorScheme,
                  fontSize: e.target.value,
                })
              )
            }
          />
        </label>
        <label>
          Font Color:
          <input
            type="color"
            value={colorScheme.fontColor}
            onChange={(e) =>
              dispatch(
                setColorScheme({
                  ...colorScheme,
                  fontColor: e.target.value,
                })
              )
            }
          />
        </label>
        </div>
      </div>
     </div>
  );
};

export default SettingsPage;
