import React from 'react';
import { BrowserRouter as Router, Route, Link,Routes } from 'react-router-dom';
import './App.scss';
import HomePage from './Components/Home';
import AboutPage from './Components/About';
import ContactPage from './Components/Contact';
import SettingsPage from './Components/Setting.js';
import Layout from './Layout/layout';
import ColorSchemeProvider from './ColorSchemeContext';
// import Header from './Layout/Header'
// import Footer from './Layout/Footer'
import './App.scss';

const App = () => {

  const [colorScheme, setColorScheme] = useState({
    backgroundColor: '',
    primaryColor: '',
    secondaryColor: '',
    fontSize: '',
    fontColor: '',
  });

  useEffect(() => {
    // Load saved settings from local storage
    const savedSettings = JSON.parse(localStorage.getItem('appSettings'));
    if (savedSettings) {
      setColorScheme(savedSettings);
    }
  }, []);

  useEffect(() => {
    // Save settings to local storage
    localStorage.setItem('appSettings', JSON.stringify(colorScheme));
  }, [colorScheme]);
  return (
    <>
     <Router>
      <Routes>
        <Route exact path="/">
          <HomePage colorScheme={colorScheme} />
        </Route>
        <Route path="/about">
          <AboutPage colorScheme={colorScheme} />
        </Route>
        <Route path="/settings">
          <SettingsPage colorScheme={colorScheme} setColorScheme={setColorScheme} />
        </Route>
        <Route path="/contact">
          <ContactPage colorScheme={colorScheme} />
        </Route>
      </Routes>
    </Router>

  </>
);
};


export default App;
