const initialState = {
  backgroundColor: '',
  primaryColor: '',
  secondaryColor: '',
  fontSize: '',
  fontColor: '',
};

const colorSchemeReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'SET_COLOR_SCHEME':
      return {
        ...state,
        ...action.payload,
      };
    default:
      return state;
  }
};

export default colorSchemeReducer;
