import React, { useState, useEffect } from 'react';

const SettingsPage = () => {
  const [backgroundColor, setBackgroundColor] = useState('');
  const [primaryColor, setPrimaryColor] = useState('');
  const [secondaryColor, setSecondaryColor] = useState('');
  const [fontSize, setFontSize] = useState('');
  const [fontColor, setFontColor] = useState('');

  useEffect(() => {
    // Load saved settings from local storage
    const savedSettings = JSON.parse(localStorage.getItem('appSettings'));
    if (savedSettings) {
      setBackgroundColor(savedSettings.backgroundColor);
      setPrimaryColor(savedSettings.primaryColor);
      setSecondaryColor(savedSettings.secondaryColor);
      setFontSize(savedSettings.fontSize);
      setFontColor(savedSettings.fontColor);
    }
  }, []);

  useEffect(() => {
    // Save settings to local storage
    const appSettings = {
      backgroundColor,
      primaryColor,
      secondaryColor,
      fontSize,
      fontColor,
    };
    localStorage.setItem('appSettings', JSON.stringify(appSettings));
  }, [backgroundColor, primaryColor, secondaryColor, fontSize, fontColor]);

  const handleBackgroundColorChange = (e) => {
    setBackgroundColor(e.target.value);
  };

  const handlePrimaryColorChange = (e) => {
    setPrimaryColor(e.target.value);
  };

  const handleSecondaryColorChange = (e) => {
    setSecondaryColor(e.target.value);
  };

  const handleFontSizeChange = (e) => {
    setFontSize(e.target.value);
  };

  const handleFontColorChange = (e) => {
    setFontColor(e.target.value);
  };
  
  const handleSchemeChange = (scheme) => {
    setSelectedScheme(scheme);

    // Update colors based on the selected scheme
    switch (scheme) {
      case 'scheme1':
        setBackgroundColor('#f8f9fa');
        setPrimaryColor('#007bff');
        setSecondaryColor('#6c757d');
        setFontSize('16px');
        setFontColor('#333');
        break;
      case 'scheme2':
        setBackgroundColor('#f3f3f3');
        setPrimaryColor('#4caf50');
        setSecondaryColor('#607d8b');
        setFontSize('18px');
        setFontColor('#222');
        break;
      case 'scheme3':
        setBackgroundColor('#eceff1');
        setPrimaryColor('#9c27b0');
        setSecondaryColor('#2196f3');
        setFontSize('14px');
        setFontColor('#555');
        break;
      case 'scheme4':
        setBackgroundColor('#ffffff');
        setPrimaryColor('#ff5722');
        setSecondaryColor('#795548');
        setFontSize('20px');
        setFontColor('#111');
        break;
      case 'scheme5':
        setBackgroundColor('#fafafa');
        setPrimaryColor('#e91e63');
        setSecondaryColor('#00bcd4');
        setFontSize('22px');
        setFontColor('#444');
        break;
      default:
        setBackgroundColor('');
        setPrimaryColor('');
        setSecondaryColor('');
        setFontSize('');
        setFontColor('');
    }
  }
  return (
    <div><div className="content">
    <h1>Settings</h1> 
    <div className="color-scheme-buttons">
      <button
        className={`color-scheme-btn ${selectedScheme === 'scheme1' ? 'active' : ''}`}
        style={{ backgroundColor: primaryColor, color: fontColor }}
        onClick={() => handleSchemeChange('scheme1')}
      >
        Scheme 1
      </button>
      <button
        className={`color-scheme-btn ${selectedScheme === 'scheme2' ? 'active' : ''}`}
        style={{ backgroundColor: secondaryColor, color: fontColor }}
        onClick={() => handleSchemeChange('scheme2')}
      >
        Scheme 2
      </button>
      <button
        className={`color-scheme-btn ${selectedScheme === 'scheme3' ? 'active' : ''}`}
        style={{ backgroundColor: backgroundColor, color: fontColor }}
        onClick={() => handleSchemeChange('scheme3')}
      >
        Scheme 3
      </button>
      <button
        className={`color-scheme-btn ${selectedScheme === 'scheme4' ? 'active' : ''}`}
        style={{ backgroundColor: primaryColor, color: secondaryColor }}
        onClick={() => handleSchemeChange('scheme4')}
      >
        Scheme 4
      </button>
      <button
        className={`color-scheme-btn ${selectedScheme === 'scheme5' ? 'active' : ''}`}
        style={{ backgroundColor: secondaryColor, color: primaryColor }}
        onClick={() => handleSchemeChange('scheme5')}
      >
        Scheme 5
      </button>
    </div>
        <h1>Settings</h1>
        <label>
          Background Color:
          <input
            type="color"
            value={backgroundColor}
            onChange={(e) => setBackgroundColor(e.target.value)}
          />
        </label>
        <label>
          Primary Color:
          <input
            type="color"
            value={primaryColor}
            onChange={(e) => setPrimaryColor(e.target.value)}
          />
        </label>
        <label>
          Secondary Color:
          <input
            type="color"
            value={secondaryColor}
            onChange={(e) => setSecondaryColor(e.target.value)}
          />
        </label>
        <label>
          Font Size:
          <input
            type="number"
            min="12"
            max="24"
            value={fontSize}
            onChange={(e) => setFontSize(e.target.value)}
          />
        </label>
        <label>
          Font Color:
          <input
            type="color"
            value={fontColor}
            onChange={(e) => setFontColor(e.target.value)}
          />
        </label>
      </div>
    </div>
  );
};

export default SettingsPage;
