import React from 'react';
import {Link} from 'react-router-dom';
import {useSelector} from 'react-redux';

const Layout = ({ children }) => {
  const colorScheme = useSelector((state) => state.colorScheme);

  const {
    backgroundColor,
    primaryColor,
    secondaryColor,
    fontSize,
    fontColor,
  } = colorScheme;

  return (
    <div className="layout">
    <div
      style={{
        backgroundColor: backgroundColor,
        color: fontColor,
      }}
    ></div><header className="header" style={{ backgroundColor: primaryColor }}>
    Header
  </header>
  <main style={{ backgroundColor: secondaryColor, fontSize: fontSize }}>
       
      <aside className="side-menu">Side Menu</aside>
      <nav className="side-menu">
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/about">About</Link>
          </li>
          <li>
            <Link to="/contact">Contact</Link>
          </li>
          <li>
            <Link to="/settings">Settings</Link>
          </li>
        </ul>
      </nav>
      </main>
      <main className="content">{children}</main>
      <footer className="footer" style={{ backgroundColor: primaryColor }}>
        Footer
      </footer>
      
    </div>
  );
};

export default Layout;
